CREATE DATABASE IF NOT EXISTS eridanus;
USE eridanus ;

DROP TABLE IF EXISTS river;
CREATE TABLE river (
    localName VARCHAR(45) NOT NULL,
    averageDischarge INT(11) DEFAULT NULL,
    catchmentBasin INT(11) DEFAULT NULL,
    length INT(11) DEFAULT NULL,
    sourceLocation VARCHAR(25) DEFAULT NULL,
    mouthLocation VARCHAR(25) DEFAULT NULL,
    isNavigable TINYINT(1) DEFAULT NULL,
    isPolluted TINYINT(1) DEFAULT NULL,
    isRegional TINYINT(1) DEFAULT NULL,
    isTributary TINYINT(1) DEFAULT NULL,
    PRIMARY KEY (localName)
)  ENGINE=INNODB DEFAULT CHARSET=UTF8;



DROP TABLE IF EXISTS country;
CREATE TABLE country (
    localName VARCHAR(45) NOT NULL,
    countryName VARCHAR(45) NOT NULL,
    PRIMARY KEY (localName , countryName),
    FOREIGN KEY (localName)
        REFERENCES river (localName)
        ON DELETE CASCADE ON UPDATE CASCADE
)  ENGINE=INNODB DEFAULT CHARSET=UTF8;



DROP TABLE IF EXISTS history;
CREATE TABLE history (
    operationID VARCHAR(45) NOT NULL,
    operationTime VARCHAR(45) DEFAULT NULL,
    operationType VARCHAR(15) DEFAULT NULL,
    localName VARCHAR(45) DEFAULT NULL,
    PRIMARY KEY (operationID),
    FOREIGN KEY (localName)
        REFERENCES river (localName)
        ON DELETE CASCADE ON UPDATE CASCADE
)  ENGINE=INNODB DEFAULT CHARSET=UTF8;


